sources.sass/components/_gallery.scss:
@mixin vertical-align {
    position: relative;
    top: 50%;
    -webkit-transform: translateY(-50%);
    -ms-transform: translateY(-50%);
    transform: translateY(-50%);
}

@mixin transition {
    -moz-transition: 0.3s ease-in-out all;
    -o-transition: 0.3s ease-in-out all;
    -webkit-transition: 0.3s ease-in-out all;
    transition: 0.3s ease-in-out all;
}

@mixin filter($filter) {
    -webkit-filter: $filter;
    -moz-filter: $filter;
    -o-filter: $filter;
}






.animated {
            -webkit-animation-duration: 2s;
            animation-duration: 2s;
            -webkit-animation-fill-mode: both;
            animation-fill-mode: both;
        }

.project-section {
    width: 100%;
    position: relative;
    height: 300px;


    .left,
    .middle,
    .right {
        height: 100%;
        width: 100%;
        display: block;
        color: #fff;
        box-sizing: border-box;
        left: 0;
        padding: 30px;
        text-align: center;
        overflow: hidden;
        // border-radius: 5px;

        @include transition;

        .content {
            @media (min-width: $sm) {
                @include vertical-align();
            }
            &__title {
                color: $color-tertiary-dark;
                font-size: 2rem;

                @media (min-width: $md) {
                font-size: 1.5rem;
                }
                @media (min-width: $lg) {
                font-size: 2rem;
                }
            }
            &__tools {
                color: #ffe493;
                font-size: 1rem;

                @media (min-width: $lg) {
                    font-size: 1.5rem;
                }
            }
        }

        @media (min-width: $sm) {
            width: 50%;
            font-size: 0.9em;
            padding: 10px;
            left: 0;
            float: left;
            position: absolute;
        }

        @media (min-width: $md) {
            width: 33.33333%;
            left: 33.33%;
            padding: 10px;
        }

        @media (min-width: $lg) {
            padding: 30px;
            font-size: 1em;
        }
    }

    &.animate {
        .left {
            left: 0;

            @media (min-width: $sm) {
                left: 50%;
            }

            @media (min-width: $md) {
                left: 0;
            }
        }

        .right {
            left: 0;

            @media (min-width: $sm) {
                left: 50%;
            }

            @media (min-width: $md) {
                left: 66.66%;
            }
        }


    }

    .title {
        background: #e18b52;

        h2 {
            margin-top: 0;
        }

        p {
            line-height: 1.55em;
            margin-bottom: 0.75em;
        }

    }

    .tiles {
        padding: 0;
        background: #fff;
        clear: both;
        display: none;
        float: none;


        img {
            width: 50%;
            float: left;
            height: auto;
            opacity: 0.7;
            @include transition;
            @include filter(grayscale(100%));


            &:hover {
                opacity: 1;
                @include filter(grayscale(0%));
            }
        }

        @media (min-width: $md) {
            display: block;
        }
    }

    .middle {
        background: slategray;
        z-index: 2;
        padding: 0;

        img {
            width: 100%;
            height: auto;
            display: block;
        }
    }
}

sources.sass/layout/_header.scss:
.header {
  background: $color-grey-dark;
  position: fixed;
  width: 100%;
  top: 0;
  z-index: 3000;
  opacity: 0.9;
}
.nav-menu {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: auto;
  padding-right: 6.5rem;
  @media only screen and (max-width: 775px) {
    padding-right: 0;
  }
}

.navbar {
  display: flex;
  justify-content: space-between !important;
  align-items: center;

  &__text {
    color: $color-orange-cv-2 !important;
    font-family: raleway, sans-serif;
    font-size: 1.5rem;
    padding: 0 1.5rem;

    &:hover,
    &:active {
      color: $color-white !important;
    }

    &--home {
      display: visible;
      padding: 0 0 0 1.5rem;

      @media only screen and (max-width: 775px) {
        display: none;
      }
    }
  }
}

.nav-item {
  margin: 1rem 0;
}

// hamburger

.hamburger {
  display: none;
  position: absolute;
  top: 1rem;
  left: 1.5rem;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  -webkit-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
  background-color: $color-tertiary-dark;
}

@media only screen and (max-width: 776px) {
  .nav-menu {
    position: fixed;
    left: -100%;
    top: 3rem;
    flex-direction: column;
    background-color: $color-black;
    width: 100%;
    border-radius: 10px;
    text-align: center;
    transition: 0.3s;
    padding: 3rem 0;
  }

  .nav-menu.active {
    left: 0%;
  }

  .hamburger {
    display: block;
    cursor: pointer;
  }

  .hamburger.active .bar:nth-child(2) {
    opacity: 0;
  }

  .hamburger.active .bar:nth-child(1) {
    transform: translateY(8px) rotate(45deg);
  }

  .hamburger.active .bar:nth-child(3) {
    transform: translateY(-8px) rotate(-45deg);
  }
}

