sources.sass/components/_slider.scss:
.slider {
  max-width: 45rem;
  height: 40rem;
  margin: 0 auto;
  position: relative;
  overflow: hidden;

  @include respond(phone) {
    height: 35rem;
  }

  @media only screen and (max-width: 475px) {
    height: 27rem;
  }
}

.slide {
  position: absolute;
  top: 0;
  width: 100%;

  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;

  /* THIS creates the animation! */
  transition: transform 1s;
}

.slide > img {
  /* Only for images that have different size than slide */
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.slider__btn {
  position: absolute;
  top: 30%;
  z-index: 10;

  border: none;
  background: rgba(255, 255, 255, 0.3);
  font-family: inherit;
  color: #333;
  border-radius: 50%;
  height: 2.5rem;
  width: 2.5rem;
  font-size: 1.5rem;
  cursor: pointer;
  // padding-bottom: 0.5rem;

  @include respond(phone) {
    display: none;
  }
}

.slider__btn--left {
  left: 6%;
  transform: translate(-50%, 0%);
}

.slider__btn--right {
  right: 6%;
  transform: translate(50%, 0%);
}

.dots {
  position: absolute;
  bottom: 15%;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
}

.dots__dot {
  border: none;
  background-color: #b9b9b9;
  opacity: 0.7;
  height: 1rem;
  width: 1rem;
  border-radius: 50%;
  margin-right: 1.75rem;
  cursor: pointer;
  transition: all 0.5s;

  /* Only necessary when overlying images */
  /* box-shadow: 0 0.6rem 1.5rem rgba(0, 0, 0, 0.7); */
}

.dots__dot:last-child {
  margin: 0;
}

.dots__dot--active {
  background-color: $color-orange-cv;
  opacity: 1;
}

.slider__icon {
  padding: 1rem;

  img {
    height: 3rem;
  }
}

sources.sass/layout/_header.scss:
.header {
  background: $color-grey-cv;
  position: fixed;
  width: 100%;
  z-index: 3000;
  opacity: 0.9;
}
.nav-menu {
  display: flex;
  justify-content: center;
  align-items: center;
  margin: auto;
  padding-right: 6.5rem;
  @media only screen and (max-width: 775px) {
    padding-right: 0;
  }
}

.navbar {
  display: flex;
  justify-content: space-between !important;
  align-items: center;
  // background-color: transparent;

  &__text {
    color: $color-orange-cv !important;
    font-family: raleway, sans-serif;
    font-size: 1.5rem;
    padding: 0 1.5rem;

    &:hover,
    &:active {
      color: $color-white !important;
    }

    &--home {
      display: visible;
      padding: 0 0 0 1.5rem;

      @media only screen and (max-width: 775px) {
        display: none;
      }
    }
  }
}

.nav-item {
  margin: 1rem 0;
}

// hamburger

.hamburger {
  display: none;
  position: absolute;
  top: 1rem;
  left: 1.5rem;
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  margin: 5px auto;
  -webkit-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
  background-color: $color-tertiary-dark;
}

@media only screen and (max-width: 776px) {
  .nav-menu {
    position: fixed;
    left: -100%;
    top: 3rem;
    flex-direction: column;
    background-color: $color-black;
    width: 100%;
    border-radius: 10px;
    text-align: center;
    transition: 0.3s;
    padding: 3rem 0;
  }

  .nav-menu.active {
    left: 0%;
  }

  .hamburger {
    display: block;
    cursor: pointer;
  }

  .hamburger.active .bar:nth-child(2) {
    opacity: 0;
  }

  .hamburger.active .bar:nth-child(1) {
    transform: translateY(8px) rotate(45deg);
  }

  .hamburger.active .bar:nth-child(3) {
    transform: translateY(-8px) rotate(-45deg);
  }
}

sources.sass/layout/_grid.scss:
.grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 0.5rem;
  margin-left: 3.5rem;

  &__column {
    display: flex;
    flex-direction: column;
  }

  &__title {
    font-size: 1.7rem;
    text-align: left;
    border-bottom: 1px solid $color-grey-dark;
    padding: 0.5rem;
    margin: 0 0.5rem 1rem 0;

    &--tools {
      width: 40%;
    }

    &--frameworks {
      width: 85%;
    }

    &--languages {
      width: 75%;
    }
  }

  &__item {
    font-size: 1.2rem;
    width: 12rem;
    display: flex;
    flex-direction: row;
    margin: 1rem 0.5rem 0 0.5rem;
    align-items: center;
  }

  img {
    width: 2.5rem;
    margin-right: 0.5rem;
  }

  @include respond(tab-port) {
    grid-template-columns: repeat(2, 1fr);
  }

  @include respond(phone) {
    grid-template-columns: 1fr;
  }
}

