  width: 25px;
  height: 3px;
  margin: 5px auto;
  -webkit-transition: all 0.3s ease-in-out;
  transition: all 0.3s ease-in-out;
  background-color: #638efb; }

@media only screen and (max-width: 776px) {
  .nav-menu {
    position: fixed;
    left: -100%;
    top: 3rem;
    flex-direction: column;
    background-color: #000;
    width: 100%;
    border-radius: 10px;
    text-align: center;
    transition: 0.3s;
    padding: 3rem 0; }
  .nav-menu.active {
    left: 0%; }
  .hamburger {
    display: block;
    cursor: pointer; }
  .hamburger.active .bar:nth-child(2) {
    opacity: 0; }
  .hamburger.active .bar:nth-child(1) {
    transform: translateY(8px) rotate(45deg); }
  .hamburger.active .bar:nth-child(3) {
    transform: translateY(-8px) rotate(-45deg); } }

.grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 0.5rem;
  margin-left: 3.5rem; }
  .grid__column {
    display: flex;
    flex-direction: column; }
  .grid__title {
    font-size: 1.7rem;
    text-align: left;
    border-bottom: 1px solid #2a2a2a;
    padding: 0.5rem;
    margin: 0 0.5rem 1rem 0; }
    .grid__title--tools {
      width: 40%; }
    .grid__title--frameworks {
      width: 85%; }
    .grid__title--languages {
      width: 75%; }
  .grid__item {
    font-size: 1.2rem;
    width: 12rem;
    display: flex;
    flex-direction: row;
    margin: 1rem 0.5rem 0 0.5rem;
    align-items: center; }
  .grid img {
    width: 2.5rem;
    margin-right: 0.5rem; }
  @media only screen and (max-width: 56.25em) {
    .grid {
      grid-template-columns: repeat(2, 1fr); } }
  @media only screen and (max-width: 37.5em) {
    .grid {
      grid-template-columns: 1fr; } }

.footer {
  z-index: 100;
  display: flex;
  flex-direction: column;
  justify-content: center; }
  .footer__text {
    display: flex;
    flex-direction: column;
    padding: 1rem;
    align-items: center; }
    .footer__text--contact-details {
      display: flex;
      flex-direction: column;
      align-items: flex-start; }
    .footer__text--email {
      font-size: 1.5rem; }
    .footer__text--phone {
      font-size: 1.5rem;
      font-family: open sans, sans-serif;
      font-weight: 100; }
    .footer__text--credits {
      color: #638efb; }
  .footer__icons {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin: 0.5rem 0 !important;
    width: 100%; }
    .footer__icons--items {
      width: 8rem; }
    .footer__icons img {
      max-width: 3rem; }
    @media only screen and (max-width: 37.5em) {
      .footer__icons {
        flex-direction: row;
        justify-content: space-evenly;
        padding-top: 1rem; } }

.contact {
  width: 100%;
  margin: 0 auto;
  width: 21rem; }
  .contact__text {
    border-bottom: 1px solid #fff;
    padding: 0.5rem;
    text-align: center; }

.fadeUp {
  animation-name: fadeUp;
  animation-delay: 0.5s;
  animation-duration: 3s;
  animation-fill-mode: both;
  animation-timing-function: ease-out; }

.background-light {
  background: #fff !important;
  color: #263546 !important;
  background-size: 100vw !important; }

.background-orange {
  background: #eeb14e !important;
  color: #000 !important;
  background-size: cover; }

.unSkew {
  transform: skewy(5deg); }

.skew {
  transform: skewy(-5deg); }
